const dbClient = require('../utils/db');
const sha1 = require('sha1');


const usersController = {
  postNew: async (req, resp)=>{
    const {email, password} = req.body;
    if (!email) {
      resp.status(400).json({error: 'Missing email'});
    }
    if (!password){
      resp.status(400).json({error: 'Missing password'});
    }
    try{
      const query = {email:email}
      const email_present = await dbClient.connection.collection('user').findOne(query)
      if (email_present){
        resp.status(400).json({error: 'Already exist'})
      }
      const hashed_password = sha1(password);
      const newUser = await dbClient.connection.collection('user').insertOne({email: email, password: hashed_password});
      return resp.status(201).json({id: newUser.insertedId, email});
    } catch(error){
      resp.status(500).json({error: 'Error while trying to add users'})
    }
  }
}

module.exports = usersController;
